require 'spec_helper'

describe('Brand') do
  it("Saves a brand") do
    brand = Brand.create({:name => "nike", :img_url => "data:image/png;base64,"})
    expect(brand.img_url).to(eq("data:image/png;base64,"))
  end
  describe(Brand) do
    it { should have_many(:shoes) }
  end
  describe(Brand) do
    it { should have_and_belong_to_many(:stores) }
  end
end
